#!/bin/bash

alt_install_java() {
priority=$1
jrebindir=$2
jremandir=$3
 /usr/sbin/alternatives --install \
 /usr/bin/java java $jrebindir/java $priority \
 --slave /usr/bin/ControlPanel ControlPanel $jrebindir/ControlPanel --slave /usr/bin/javaws javaws $jrebindir/javaws --slave /usr/bin/jcontrol jcontrol $jrebindir/jcontrol --slave /usr/bin/jjs jjs $jrebindir/jjs --slave /usr/bin/keytool keytool $jrebindir/keytool --slave /usr/bin/orbd orbd $jrebindir/orbd --slave /usr/bin/pack200 pack200 $jrebindir/pack200 --slave /usr/bin/policytool policytool $jrebindir/policytool --slave /usr/bin/rmid rmid $jrebindir/rmid --slave /usr/bin/rmiregistry rmiregistry $jrebindir/rmiregistry --slave /usr/bin/servertool servertool $jrebindir/servertool --slave /usr/bin/tnameserv tnameserv $jrebindir/tnameserv --slave /usr/bin/unpack200 unpack200 $jrebindir/unpack200 --slave /usr/local/share/man/man1/java.1 java.1 $jremandir/java.1 --slave /usr/local/share/man/man1/javaws.1 javaws.1 $jremandir/javaws.1 --slave /usr/local/share/man/man1/jjs.1 jjs.1 $jremandir/jjs.1 --slave /usr/local/share/man/man1/keytool.1 keytool.1 $jremandir/keytool.1 --slave /usr/local/share/man/man1/orbd.1 orbd.1 $jremandir/orbd.1 --slave /usr/local/share/man/man1/pack200.1 pack200.1 $jremandir/pack200.1 --slave /usr/local/share/man/man1/policytool.1 policytool.1 $jremandir/policytool.1 --slave /usr/local/share/man/man1/rmid.1 rmid.1 $jremandir/rmid.1 --slave /usr/local/share/man/man1/rmiregistry.1 rmiregistry.1 $jremandir/rmiregistry.1 --slave /usr/local/share/man/man1/servertool.1 servertool.1 $jremandir/servertool.1 --slave /usr/local/share/man/man1/tnameserv.1 tnameserv.1 $jremandir/tnameserv.1 --slave /usr/local/share/man/man1/unpack200.1 unpack200.1 $jremandir/unpack200.1
}

